mapper.select:
SELECT *
FROM comment
WHERE related_post_id = #{postId}
ORDER BY id DESC
  LIMIT #{offset}, #{pageSize}

mapper.insert:
insert into comment (content, reviewer_name, reviewer_no, 
  like_count, related_post_id, related_comment_id
  )
values (#{content,jdbcType=VARCHAR}, #{reviewerName,jdbcType=VARCHAR}, #{reviewerNo,jdbcType=BIGINT}, 
  #{likeCount,jdbcType=BIGINT}, #{relatedPostId,jdbcType=INTEGER}, #{relatedCommentId,jdbcType=INTEGER}
  )
