mapper.insert:
insert into selections (student_id, course_id, semester, 
  selection_time, `status`, is_seckill
  )
values (#{studentId,jdbcType=INTEGER}, #{courseId,jdbcType=INTEGER}, #{semester,jdbcType=VARCHAR}, 
  #{selectionTime,jdbcType=TIMESTAMP}, #{status,jdbcType=OTHER}, #{isSeckill,jdbcType=INTEGER}
  )

mapper.update:
UPDATE selections
SET status = 'DROPPED'
WHERE id = #{id}

mapper.select:
SELECT EXISTS(
  SELECT 1 FROM selections
  WHERE student_id = #{studentId} AND course_id = #{courseId}
  LIMIT 1
)
